mapper.select:
select coupon_id, user_id, name, start_date, end_date, discount
from coupon
where coupon_id=#{coupon_id} and user_id=#{user_id}

mapper.insert:
UPDATE  reservation
SET user_id=#{user_id}, menu_id=#{menu_id}, group_id=#{group_id}, state=#{state}, comment=#{comment}
WHERE reservation_id=#{reservation_id}
